import { Component, OnInit } from '@angular/core';
import { FormControl, FormGroup } from '@angular/forms';
import { Observable, Subject, map, startWith, switchMap } from 'rxjs';
import { ExchangeService } from '../exchange/exchange.service';

@Component({
    selector: 'app-exchange-form',
    templateUrl: './exchange-form.component.html',
    styleUrls: ['./exchange-form.component.css']
})
export class ExchangeFormComponent implements OnInit {

    exchangeForm = new FormGroup({
        currency: new FormControl(),
        date: new FormControl('')
    });

    constructor(
        private exchangeService: ExchangeService
    ) { }

    myControl = new FormControl('');
    dateControl = new FormControl<Date>(new Date());
    options: string[] = ['One', 'Two', 'Three'];
    filteredOptions$!: Observable<string[]>;
    lel: string = 'fff';
    options$!: Observable<any>;
    rate$!: Observable<any>;

    ngOnInit() {
        this.options$ = this.exchangeService.getAllCurrencies();
        this.filteredOptions$ = this.myControl.valueChanges.pipe(
            startWith(''),
            map(value => this._filter(value || '')),
        );
        this.exchangeForm.valueChanges.subscribe((value) => {
            this.test();
        });
    }

    private _filter(value: string): string[] {
        const filterValue = value.toLowerCase();

        return this.options.filter(option => option.toLowerCase().includes(filterValue));
    }

    test() {
        this.rate$ = this.exchangeService.getSelectedExchangeRate('RUB', this.dateControl.value!);
    }

}
